mapper.update:
update resumes
set education=#{education}, experience=#{experience}, skills=#{skills}, is_completed=#{isCompleted},
    preferred_location=#{preferredLocation}, salary_expectation=#{salaryExpectation}, is_public=#{isPublic},
    title=#{title}, core_competency=#{coreCompetency}, desired_position=#{desiredPosition}, motivation=#{motivation}
where id=#{id}
